/* This file contains the home page. */
import React,{ useState } from 'react';
import { Text, View, Button,StyleSheet } from 'react-native';
import PostScreen from './PostScreen';
import {Container,Content,Icon} from 'native-base';


function HomeScreen({navigation}) {
  const posts = useState([
        {_renderPosts}
  ]);
  const _renderPosts = () => {
    const posts = this.state.posts.map(post => {
      return <PostScreen describe = {post.summary} poster ={post.background_image} key={post.id}/>
    })
      return posts
  }
  const _callApi = () => { 
    return fetch('https://yts.mx/api/v2/list_movies.json?quality=3D')
    .then(response => response.json())
    .then(json => json.data.movies)
    .catch(err => console.log(err))
  }
  const _getPosts = async () => {
  const posts = await this._callApi();
  this.setState({
    posts
  })
}

    return (
      <Container style= {styles.container}>
      <Content>
          {_getPosts}

          {_renderPosts}

      </Content>
    </Container>
    );
}

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex :1,

  }
});
