import React from 'react';
import { Text, View, StyleSheet,Image} from 'react-native';
import {Card, CardItem, Thumbnail,Body,Left, Right, Button, Icon} from 'native-base';
import FontAwesome from 'react-native-vector-icons/FontAwesome';




function PostScreen() {
    return (
      <Card>
          <CardItem>
             <Left>
                  <Thumbnail source={require('../assets/favicon.png')}/>{/*user image*/}
                 <Body>

                      <Text>Varun</Text>
                      <Text note>Jan 15,2020</Text>

                 </Body>

             </Left>
           </CardItem>
           <CardItem>
               <Image source={require('../assets/icon.png')} 
               style={{height:200,width:null,flex:1}}/>
           </CardItem>
           <CardItem>
               <Body>
                   <Text>
                   This tutorial explains how to set text color in react native application. We will make this example as simple as possible, it may help you to build more understanding on react native to set color of text content. You need to use color attribute/property of stylesheet design, in order to set text color in your Text component.
                   </Text>
               </Body>
           </CardItem>
           <CardItem style={{height:45}}>
             <Left>
                 <Button transparent>
                     <FontAwesome name="heart-o"/>
                     <Text>12</Text>
                 </Button>
                 <Button transparent>
                     <FontAwesome name="retweet"/>
                     <Text> 13</Text>
                 </Button>
                 <Button transparent>
                     <FontAwesome name="share"/>
                 </Button>
                 <Right>
                     <Button style={styles.readMore}>
                         <Text style ={{color:"blue"}}>READ MORE</Text>
                     </Button>
                 </Right>
             </Left>
           </CardItem>
      </Card>
    );
}

export default PostScreen;

const styles = StyleSheet.create({
    container: {
        flex:1,
        alignItems: 'center',
        justifyContent: 'center'
    },
    readMore : {
      backgroundColor:'white'  
    }
});
