import { Text, View, Button,StyleSheet,SafeAreaView,FlatList,ActivityIndicator } from 'react-native';
import PostScreen from './PostScreen';
import {Container,Content,Icon} from 'native-base';


const postURL = "https://yts.mx/api/v2/list_movies.json?quality=3D";
export async function getPosts() {
      let posts = await fetch('postURL')
      .then((response) => response.json())
      .then(json)
      .catch((err) => console.log(err));
}


function HomeScreen({navigation}) {
    const [isLoading,setLoading] = useState(true);
    const [data,setData] = useState([]);

    useEffect(() => {
      fetch(postURL).then((response) => response.json()).then((json) =>
      setData(json.data.movies)).catch((error) => console.log(error))
      .finally(setLoading(false));
    })

    return (
      <SafeAreaView style={styles.container}>
        {isLoading ? <ActivityIndicator/> : 
            <PostScreen/>

        }
      </SafeAreaView>
    );
}

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex :1,

  }
});
